package ex01.control.loop;

import java.util.Scanner;

public class Ex03_while {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * int i =0; while(i < 10){ i++; System.out.println(i); }
		 */

		boolean run = true;
		int speed = 0;

		while (run) {
			System.out.println("장비를 작동 합니다. \n 1.증속 \n 2.감속 \n 3.정지 \n --------------------------------------");
					System.out.println("입력");
			
			int key = new Scanner(System.in).nextInt();

			if (key == 1) {
				speed++;
				System.out.println("속도가 증가 됐습니다. 지금 속도는 " + speed + "입니다.\n");

			} else if (key == 2) {
				speed--;
				System.out.println("속도가 감속 됐습니다. 지금 속도는 " + speed + "입니다.\n");

			} else if (key == 3) {
				System.out.println("장비를 정지합니다. 정지하겠습니다. 으 아니 안돼잖아? 앙돼?!\n");
				
				
				
			}
		}

	}

}
